import React, { useState } from 'react';
import Navbar from './Navbar';
import LoginForm from './Login'; // Import the LoginForm component

const Home = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(false); // State to manage authentication

    return (
        <div>
            <Navbar isAuthenticated={isAuthenticated} />
            <h1 style={{ textAlign: 'center', margin: '20px 0' }}>Task Manager App</h1>
            <div style={{ textAlign: 'center', marginBottom: '20px' }}>
                <img 
                    className='centered-element' 
                    src={"./images/3894977.jpg"} 
                    style={{ 
                        width: '50%', 
                        height: 'auto', 
                        borderRadius: '12px',
                        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)' 
                    }} 
                    alt={"home-img"} 
                />
            </div>
            <h2 style={{ textAlign: 'center', padding: '0 20px' }}>
                <strong>Task Manager App</strong> <br></br> 
                An app that helps you manage tasks by letting you add users, add new tasks, and assign the tasks to your users.
            </h2>
            <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
                <LoginForm setIsAuthenticated={setIsAuthenticated} /> {/* Render the LoginForm component */}
            </div>
            <footer style={{ backgroundColor: '#343a40', color: '#fff', padding: '20px 0', textAlign: 'center', marginTop: '40px' }}>
                <div style={{ marginBottom: '20px' }}>
                    <a href="https://www.facebook.com" target="_blank" rel="noopener noreferrer" style={{ margin: '0 10px', color: '#fff' }}>Facebook</a>
                    <a href="https://www.twitter.com" target="_blank" rel="noopener noreferrer" style={{ margin: '0 10px', color: '#fff' }}>Twitter</a>
                    <a href="https://www.linkedin.com" target="_blank" rel="noopener noreferrer" style={{ margin: '0 10px', color: '#fff' }}>LinkedIn</a>
                    <a href="https://www.instagram.com" target="_blank" rel="noopener noreferrer" style={{ margin: '0 10px', color: '#fff' }}>Instagram</a>
                </div>
                <div>
                    <p>&copy; 2024 Your Company. All rights reserved.</p>
                    <p><a href="/privacy-policy" style={{ color: '#fff' }}>Privacy Policy</a> | <a href="/terms-of-service" style={{ color: '#fff' }}>Terms of Service</a></p>
                </div>
            </footer>
        </div>
    );
};

export default Home;
