import React, { useState } from 'react';
import Navbar from './Navbar';
import LoginForm from './Login'; // Import the LoginForm component

const Home = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(false); // State to manage authentication

    return (
        <div style={{ 
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            height: '100vh',
            backgroundColor: '#f8f9fa',
            padding: '20px',
            textAlign: 'center'
        }}>
            <Navbar isAuthenticated={isAuthenticated} />
            <h1 style={{ 
                fontSize: '3rem',
                color: '#343a40',
                marginBottom: '20px'
            }}>Task manager app</h1>
            <img 
                className='centered-element' 
                src={"https://i.pinimg.com/564x/ef/06/00/ef0600c28f1bcd44c7a9a7093d98c334.jpg"} 
                style={{ 
                    width: '200px',
                    borderRadius: '50%',
                    marginBottom: '30px',
                    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)'
                }} 
                alt={"home-img"} 
            />
            <h2 style={{ 
                fontSize: '1.5rem',
                color: '#6c757d',
                lineHeight: '1.6',
                marginBottom: '30px'
            }}>
                <strong>Task manager app</strong> <br></br> 
                An app that helps you manage tasks by letting you add users, add new tasks and assign the tasks to your users.
            </h2>
            <LoginForm 
                setIsAuthenticated={setIsAuthenticated} 
                style={{ 
                    maxWidth: '400px',
                    width: '100%',
                    backgroundColor: '#ffffff',
                    border: '1px solid #dee2e6',
                    borderRadius: '8px',
                    padding: '20px',
                    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)'
                }} 
            />
        </div>
    );
};

export default Home;
