import React, { useState, useEffect } from 'react';
import Navbar from './Navbar';
// import { Button } from 'semantic-ui-react'
import { Dropdown } from 'semantic-ui-react'

function AssignmentForm() {
  const [tasks, setTasks] = useState([]);
  const [users, setUsers] = useState([]);
  const [selectedTask, setSelectedTask] = useState('');
  const [selectedUser, setSelectedUser] = useState('');
  const [status, setStatus] = useState(''); // New state for status

  useEffect(() => {
    fetchTasks();
    fetchUsers();
  }, []);

  function fetchTasks() {
    fetch('https://task-app-server-07x5.onrender.com/tasks')
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to fetch tasks');
        }
        return response.json();
      })
      .then(data => {
        setTasks(data);
      })
      .catch(error => {
        console.error('Error fetching tasks:', error);
      });
  };

  function fetchUsers() {
    fetch('https://task-app-server-07x5.onrender.com/users')
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to fetch users');
        }
        return response.json();
      })
      .then(data => {
        setUsers(data);
      })
      .catch(error => {
        console.error('Error fetching users:', error);
      });
  };

  function handleAssignTask(event) {
    event.preventDefault();
    const assignmentData = { "task_id": selectedTask, "user_id": selectedUser, "status": status};
    console.log(assignmentData)
    fetch('https://task-app-server-07x5.onrender.com/assignments', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(assignmentData),
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to assign task');
        }
      })
      .catch(error => {
        console.error('Error assigning task:', error);
      });
  };

  return (
    <div class="center aligned content" >
      <Navbar/>
      <h2 class="center aligned content" >Assign Task</h2>
      <form onSubmit={handleAssignTask}>
        <label>
        const DropdownExampleSelection = () => (
            <Dropdown
            placeholder='Select Friend'
            fluid
